void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv;
    vec2 uvs = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    float t = iTime + abs(length(uvs) * 5.0) * sin(iTime / 10.0) ;
    float s = sin(t);
    float c = cos(t);
    uv.x = c * uvs.x - s * uvs.y;
    uv.y = s * uvs.x + c * uvs.y;
	fragColor = texture(iChannel0,uv) * length(uvs) * 1.5 ;
}
