void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 sh=texture(iChannel0,fragCoord/iResolution.xy);
	if(sh.x==0.0)
    {
        fragColor=vec4(1.0,0.0,0.0,1.0);
    }
    else if(sh.x==0.1)
    {
        fragColor=vec4(0.0,1.0,0.0,1.0);
    }
    else if(sh.x==0.2)
    {
        fragColor=vec4(0.0,0.0,1.0,1.0);
    }
 }

bool sample(vec2 p)
{
    return texture(iChannel0,p/512.0).x>=0.5;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 l=iMouse.xy;
    vec2 p=fragCoord;
    vec2 d=l-p;
    d/=max(abs(d.x),abs(d.y));
    if(sample(p))
    {
        fragColor=vec4(0.0,0.0,0.0,1.0);
    }
    else
    {
        fragColor=vec4(0.1,0.0,0.0,1.0);
    	for(int x=0;x<1;x+=0)
    	{
	        if(sample(p))
	        {
	            fragColor=vec4(0.2,distance(fragCoord,p),0.0,1.0);
	            break;
	        }
	        if(distance(l,p)<1.0)
	        {
	            break;
	        }
	        p+=d;
	    }
    }
}
