void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Move the center of the concentric circles in a small circle
    vec2 center = vec2(0.5, 0.25);
    center += vec2(sin(iTime) * .25, cos(iTime) * .25);

	vec2 uv = fragCoord / iResolution.x;
    // Create the rings by running the distance through some simple math
    // and checking if the result is odd.
    if (mod(float(int(distance(uv,center) * 25.)), 2.) == 1.){
        fragColor = vec4(0.);
    }
    else {
		fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    }
}
