const int NUM = 50;
const int repeat = 5;


float getCol(vec2 uv){
	float dark = 1.0;
    for(int i = 0;i<NUM;i++){
        float fi = float(NUM-i)/float(NUM);
        float rad = fi/5.0+.01;
    	vec2 circ = vec2(.25*(1.-fi)*(float(i%2==0)*2.-1.),0);
        if(length(circ-uv)<rad){
            dark = 0.0;
            if(length(circ-uv)-rad>-.003){
                dark = 1.0;
            }
        }

    }
   return dark;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
    uv*=.5;

    float pixSize = .001;
    float dark = 0.;

    for(int i = 0; i < repeat; i++) { 
        for(int j = 0; j < repeat; j++) { 
    		dark+=getCol(uv+vec2(i,j)*pixSize/float(repeat));
        }
    }

    dark *= 1.0/float(repeat*repeat);

    vec3 col = vec3(dark);
     fragColor = vec4(col,1.0);
}
