void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;

    float red;
    float green;
    float blue;

    float dist;
    vec2 dif;

    float blur;

    float x;
    float y;

    float offset;

    //general

    blur = 0.005 + (sin(iTime*0.3)+1.0)/10.0;
    offset = sin(iTime*1.3) * 0.4;

    //green circle

    x = sin(radians(0.0) + iTime / 3.0) * offset;
    y = cos(radians(0.0) + iTime / 3.0) * offset;

    dif = vec2(x-uv.x, y-uv.y);
    dist = length (dif);

    green = smoothstep(0.3,0.3-blur,dist);


    //red circle

    x = sin(radians(120.0) + iTime / 3.0) * offset;
    y = cos(radians(120.0) + iTime / 3.0) * offset;

    dif = vec2(x-uv.x, y-uv.y);
    dist = length (dif);

    red = smoothstep(0.3,0.3-blur,dist);


    //blue circle

    x = sin(radians(240.0) + iTime / 3.0) * offset;
    y = cos(radians(240.0) + iTime / 3.0) * offset;

    dif = vec2(x-uv.x, y-uv.y);
    dist = length (dif);


    blue = smoothstep(0.3,0.3-blur,dist);
    //Output
    fragColor = vec4(red,green,blue,1.0);
}
