void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;


    float a = atan(uv.y/uv.x);

    float r = pow(pow(uv.x * uv.x, 2.0) + pow(uv.y * uv.y, 2.0), 1.0/4.0);


    vec2 p = vec2(1.0/r + iTime * 1.0 + texture(iChannel1, vec2(0.15, 0.0)).x / 2.0, a / 3.14159 * 2.0 + 0.5);

    vec3 col = texture(iChannel0, p).xyz * vec3(1.0, 0.0, 0.0) * clamp(r, 0.0, 1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    uv.x -= sqrt(abs(uv.y)) * 0.5;
    float heart = smoothstep( 0.5, 0.4, length(uv));

    fragColor = vec4(vec3(heart), 1.0);

}
