void fStops(inout vec3 col, vec2 uv)
{
	float c0 = texture(iChannel0, vec2(0.06128 * uv.y * uv.x)).r;
    float c1 = texture(iChannel0, vec2(uv.x, uv.y)).r/0.851;
    float c2 = texture(iChannel0, vec2(40.8 * uv.x * uv.y) / 2.).r * 0.75;

    col = vec3( sin(c0) * 1.6, cos(c1 * iTime) * 0.125, tan(c2) ) * 0.61;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;

    fStops(col, uv);

    fragColor = vec4(col,1.0);
}
