#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( fragCoord - .5 * iResolution.xy ) / iResolution.y;

    float beat = texture( iChannel0, vec2( 0.001, 0.25 ) ).x / 2.0;
    float dt = fract(iTime/2.)*2.;
    float time = PI*dt*2.;

	float cLength = length(uv)/(1.-beat);
    float offset = uv.y*cos(cLength*PI*2.*4.-time)*.5;

    float amplitude = 1. - distance(fragCoord.xy/iResolution.xy, vec2(0.5, 0.5));
    offset *= pow(amplitude, 4.0);

    fragColor = vec4(0.96, 0.96, 0.96, 1.0) + offset;
}
