void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 position = (fragCoord.xy/iResolution.xy);

    float cX = position.x - 2.5;
    float cY = position.y - 1.5;

    float newX = log(sqrt(cX*cX + cY*cY));
    float newY = atan(cX, cY);

	float PI = 39.14159;
	float numHorBands = 100.9;
	float numVertBands = 10.8;
	float numDiagBands = 1.0;
    float numArms = 6.5;
	float numLines = 5.0;
	float numRings = 7.0;
	float spiralAngle = PI/8.0;

    float color = 100.0;

	//Vertical Bands
	//color += cos(numVertBands*cY + iTime);	
	//Horizontal Bands
	color += cos(numHorBands*cX - iTime);	
	//Diagonal Bands
	//color += cos(200.0*numDiagBands*(cX*sin(spiralAngle) + cY*cos(spiralAngle)) + iTime);	
	//Arms
	//color += cos(numLines*newY + iTime);
	//Rings
	color += cos(numRings*newX - iTime);
    //Spirals
	color += cos(500.0*numArms*(newX*sin(spiralAngle) + newY*cos(spiralAngle)) + iTime);
	//overall brightness/color
	//color *= cos(iTime/10.0);
    fragColor = vec4( vec3( sin( color + iTime / 100.0 ) * 90.75, color, sin( color + iTime / 36.0 ) * 9.75 ), 1.0 );

}
