float dis(vec2 a, vec2 b) {
    float dx = a.x - b.x;
    float dy = a.y - b.y;
	return sqrt(dx*dx + dy*dy);
}

float u(float a, float b) {
    return max(a, b);
}

float circle(vec2 uv, vec2 p, float r) {
	return -1.0 * sign(length(uv - p)  - r);
}

float box( vec2 uv, vec2 p, vec2 s )
{
  vec2 res = abs(uv - p) - s;
  return -1.0 * sign(max(res.x,res.y));
}

vec2 rot(vec2 uv, float a) {
    uv -= vec2(0.5, 0.5);
	float s = sin(a);
    float c = cos(a);
    return vec2(uv.x*c + uv.y*s, uv.x*s + uv.y*c) + vec2(0.5, 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = iTime;
    float hit = u(circle(uv, vec2(0.5 + sin(t*0.75), 0.5 + sin(t)), 0.3),
                  circle(uv, vec2(0.1 + 0.5*sin(t*0.3), 0.1 + 0.6*cos(t*0.4)), 0.1));
    hit = u(hit, box(rot(uv, t), vec2(0.5, 0.5), vec2(0.1,0.7)));
    vec3 col = vec3(1,0,0);

    float c = max(sin(t*1.0 + uv.x * 20.0), sin(t*1.0 + uv.y * 20.0));
    hit = max(0.0, hit);
    c += hit * (0.5 * c + 0.5);
    vec3 wCol = vec3(c,c,c);


    wCol.x = max(wCol.x, 0.0);
    wCol.y = max(wCol.y, 0.0);
    wCol.z = max(wCol.z, 0.0);

    col.x = max(col.x, 0.0);
    col.y = max(col.y, 0.0);
    col.z = max(col.z, 0.0);


	fragColor = vec4(/*col * hit + wCol  * (1.0 - hit)*/wCol, 1.0);


    vec2 p = vec2(0.5 + sin(iTime*0.1),0.5+ sin(iTime*0.1));
    vec2 p2 = vec2(0.3+ cos(iTime*0.2),0.7+ sin(iTime*0.15));
    vec2 p3 = vec2(1.0+ sin(iTime*0.06),1.0+ sin(iTime*0.07));
	fragColor += (1.0-hit) * 0.1 * vec4(sin(dis(uv, p3) * 100.0 - 3.0*iTime) + 
                     cos(dis(uv, p2) * 100.0 - 2.0*iTime) + 
                     sin(dis(uv, p) * 100.0 - 1.0*iTime), 

                     sin(dis(uv, p3) * 100.0 - 3.0*iTime) + 
                     sin(dis(uv, p2) * 100.0 - 2.0*iTime) + 
                     cos(dis(uv, p) * 100.0 - 1.0*iTime), 

                     sin(dis(uv, p3) * 100.0 - 3.0*iTime) + 
                     sin(dis(uv, p2) * 100.0 - 2.0*iTime) + 
                     sin(dis(uv, p) * 100.0 - 1.0*iTime)
                     ,1.0);

    //fragColor += (sign(sin(t*2.0) - 0.9) + 1.0) * 0.5 * 0.5 * vec4(-fragColor.x, 1, -fragColor.z,1.0);

}
