float dis(vec2 a, vec2 b) {
    float dx = a.x - b.x;
    float dy = a.y - b.y;
	return sqrt(dx*dx + dy*dy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = vec2(0.5 + sin(iTime*0.1),0.5+ sin(iTime*0.1));
    vec2 p2 = vec2(0.3+ cos(iTime*0.2),0.7+ sin(iTime*0.15));
    vec2 p3 = vec2(1.0+ sin(iTime*0.06),1.0+ sin(iTime*0.07));
	fragColor = vec4(sin(dis(uv, p3) * 100.0 - 3.0*iTime) + 
                     cos(dis(uv, p2) * 100.0 - 2.0*iTime) + 
                     sin(dis(uv, p) * 100.0 - 1.0*iTime), 

                     sin(dis(uv, p3) * 100.0 - 3.0*iTime) + 
                     sin(dis(uv, p2) * 100.0 - 2.0*iTime) + 
                     cos(dis(uv, p) * 100.0 - 1.0*iTime), 

                     sin(dis(uv, p3) * 100.0 - 3.0*iTime) + 
                     sin(dis(uv, p2) * 100.0 - 2.0*iTime) + 
                     sin(dis(uv, p) * 100.0 - 1.0*iTime)
                     ,1.0);
}
