void mainImage( out vec4 o, vec2 uv ){
    uv /= iResolution.xy;

    float t = iTime,
         s1 = sin(t*.2 +1.5) / 6.  +.4,
         s2 = sin(t*.44+.5 ) / 3.2 +.4,
         s3 = sin(t*.29 +2.) / 7.9 +.4,
         s4 = sin(t*.4  +1.) / 5.1 +.5;
          t = t*.1 + 10.*length(1.-2.*uv);

    o.b =   (1. - cos( atan(s1-uv.x, s2-uv.y) * (5. +  4.*sin(t) ) )
                - cos( atan(s3-uv.x, s4-uv.y) * (6. + 12.*sin(t) ) )
             ) /3.;
}
