//Efficient version, Didnt even know some of this stuff was possible, thank you FabriceNeyret2.
void mainImage( out vec4 fragColor,vec2 fragCoord )
{
    fragColor = vec4(0.0);
    float t = -iTime*3.;
    vec2 uv = fragCoord - iResolution.xy/2.;
    float dist = length(uv)/5.* (.5+.5*cos(t/10.0));
    float om = atan(uv.y, uv.x) + dist;
    for (float i = 0.; i<mix;i++){
        float omeg = om + pi/2./mix * i;
	    vec4 m =  omeg + t + tau*vec4(0,1,2,0)/3.;
        m = mod( pi + pi/2.-m, tau) - pi ;
       fragColor += (.5-1.0/tau*m ) /mix;
    }
}
