vec2 rotate(in vec2 uv, float a)
{
    a = radians(a);
	return (vec2(uv.x * cos(a) - uv.y * sin(a), uv.x * sin(a) + uv.y * cos(a)));
}

bool isInCircle(vec2 uv, vec2 c, float r)
{
    return length(uv - c) <= r;
}

bool isOnCircle(vec2 uv, vec2 c, float r)
{
    return isInCircle(uv, c, r) && length(uv - c) > r - 0.005;
}

bool isNeedle(vec2 no, float w, float h, float a)
{
    no = rotate(no, a);
    w /= 2.0;
    return (no.x > -w && no.x < w && no.y > 0.0 && length(no) < h);
}

#define EPSILON 0.001

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x = uv.x * iResolution.x / iResolution.y;
    vec3 c = vec3(0.0);

    vec2 un = normalize(uv);
	if (isInCircle(uv, vec2(1.0 - mod(iTime * 2.0, 1.2), 0.0) * 2.0, 0.2))
        c = vec3(1.0);
    if (length(uv) < 1.0)
    {
    	    float mousea = 30.0 * (cos(iTime * 10.0)/ 2.0 + 0.5);
    	    if (degrees(atan(un.y, un.x)) > mousea || degrees(atan(un.y, un.x)) < -mousea)
    	    	c = vec3(1.0, 1.0, 0.0);
    	if (isInCircle(uv, vec2(0.4, 0.4), 0.2) && rotate(uv, 0.0).y > 0.4)
    	    if ((degrees(atan(un.y, un.x)) > 50.0  || degrees(atan(un.y, un.x)) < 49.0) && length(un) > 0.1)
    	    	c = vec3(0.0);
    }
    float as = floor(iDate.w * 360.0 / 60.0);
    float ah = (iDate.w / 3600.0) * 360.0 / 12.0;
    float am = (iDate.w / 60.0) * 360.0 / 60.0;
    if (isNeedle(uv, 0.03, 0.65, am))
        c = vec3(0.0, 0.0, 1.0);
    if (isNeedle(uv, 0.03, 0.4, ah))
        c = vec3(0.0, 1.0, 0.0);
   	if (isNeedle(uv, 0.02, 0.8, as))
  		c = vec3(1.0, 0.0, 0.0);
    fragColor = vec4(c, 1.0);
}
