vec2 cell(vec2 fragCoord, vec2 pixel, vec2 dir, float scale)
{
    pixel *= dir;

    // remove screen border of domain
    if (fragCoord.x + pixel.x > iResolution.x) fragCoord.x = 0.;
    if (fragCoord.y + pixel.y > iResolution.y) fragCoord.y = 0.;
    if (fragCoord.x + pixel.x < 0.0) fragCoord.x = iResolution.x;
    if (fragCoord.y + pixel.y < 0.0) fragCoord.y = iResolution.y;

	vec2 uv = (fragCoord + pixel) / iResolution.xy;
    return texture(iChannel0, uv).rg * scale;
}

vec2 laplacian2D(vec2 fragCoord, vec2 dir, float a, float b) 
{
    float st = 1.;
    a /= 4.;
    b /= 4.;
    return 
        cell(fragCoord, vec2(0., -st), dir, a) +
        cell(fragCoord, vec2(0., st), dir, a) +
        cell(fragCoord, vec2(st, 0.), dir, a) +
        cell(fragCoord, vec2(-st, 0.), dir, a) +
        cell(fragCoord, vec2(-st, -st), dir, b) +
        cell(fragCoord, vec2(-st, st), dir, b) +
        cell(fragCoord, vec2(st, -st), dir, b) +
        cell(fragCoord, vec2(st, st), dir, b) -
        cell(fragCoord, vec2(0., 0.), dir, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = mod(iTime,3.14159*2.);

    // thanks to shane for this lighting model

    vec2 lp0 = laplacian2D(fragCoord, vec2(0), .8, .2);
    vec2 lp1 = laplacian2D(fragCoord, vec2(-1), .8, .2);

    float c = lp0.g;
    float c2 = lp1.g;

   	vec3 col = vec3(1)*c;
    col += vec3(.2, .4, 1)*max(c2*c2*1.5 - c*c, 0.0)*iResolution.y*.2;

    fragColor = (vec4(col, 1.));
}

