void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 mo = iMouse.xy / iResolution.xy;

    fragColor = vec4(0,0,0,0);

   	if (iFrame < 30)
    {
        uv = (fragCoord*2. - iResolution.xy)/iResolution.y;
    	//if (length(uv) < 0.05)
		//	fragColor = mix(vec4(0,1,0,1), vec4(1,0,0,1), length(uv)); 

        fragColor = vec4(texture(iChannel1, uv).rg, 0.0, 0.0);
    }
    else if (iMouse.z > 0.0)
	{
		fragColor = texture(iChannel0, uv);
        if (length((uv-mo)*vec2(iResolution.x/iResolution.y,1)) < 0.1)
			fragColor = mix(vec4(0,1,0,1), vec4(1,0,0,1), length(uv-mo)); 
	}
    else
    {
        float a = float(iFrame) * 0.1;

		vec2 dir = normalize(vec2(a+cos(a),a-sin(a)));

        dir = vec2(1);

        vec2 diffusionCoef = vec2(1.,.01);
        float feedCoef = 0.3;
        float killCoef = 0.3;

        vec2 ab = cell(fragCoord, vec2(0,0), vec2(0,0), 1.);
        vec2 lp = laplacian2D(fragCoord, dir, .3, .7);

        float reaction = ab.x * ab.y;
        vec2 diffusion = diffusionCoef * lp;
        float feed = feedCoef * (1. - ab.x);
        float kill = (feedCoef + killCoef) * ab.y;

        ab += diffusion + vec2(feed - reaction, reaction - kill);

    	fragColor = vec4(clamp(ab,0.,1e4),0.0,1.0);
    }
}

