/*
	-------------
	Checker waves
	-------------

    Just for playing a bit with sinus, uv offset and patterns repetition

	Francois 'CoyHot' Grassard - 2017
*/

#define DispStrenght 0.2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((fragCoord.xy / iResolution.xy)-0.5)/vec2(iResolution.y / iResolution.x, 1);

    uv.y += sin(((uv.x+iTime)*0.75)*0.75);
    float c = sin((uv.x-iTime)*20.0)/sin(uv.y*20.)*100.;

	fragColor = vec4(clamp(c,0.0,1.0));
}
