renderpass.code:
mat2 r2d(float a) {
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}


float sc(vec3 p) {
	p = abs(p);
	float da = max(p.x, p.y);
	float db = max(p.y, p.z);
	float dc = max(p.z, p.x);
	return min(da, min(db, dc));
}

vec2 amod(vec2 p, float m) {
	float a = mod(atan(p.x, p.y), m) - m * .5;
	return vec2(cos(a), sin(a)) * length(p);
}

float map(vec3 p) {
	mat2 r = r2d(iTime);

	p.xy = abs(amod(p.xy, .785));

	p.z = mod(p.z, 8.) - 4.;

    //p.y = mod(p.y, 2.) - 1.;

	for (int i = 0; i < 5; i++) {

        //p.xy*=r2d(.48);

        p =  abs(reflect(p, normalize(vec3(.64, -.4, .7)))) -2.3;


		p.y = abs(p.y) - .1;
	}


	return 1.7*sc(p) - .5;
    //return .9*sc(p) - .5;
    //return 1.1*sc(p) - .5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;
	uv.x *= iResolution.x / iResolution.y;

    // mode 1
    //vec3 ro = vec3(0, 0, -iTime*10.), p;
	vec3 rd = normalize(vec3(uv * r2d(iTime*.6), -1));

    // mode 2
    vec3 ro = vec3(cos(iTime), sin(iTime), -iTime*10.), p;
    //vec3 rd = normalize(vec3(uv, -1));


	p = ro;
	int ri;
	for (int i = 0; i < 100; i++) {
		ri = i;
		float d = map(p);
		if (d < .001) break;
		p += d * rd;
	}

    // b&w simple render
	//fragColor = vec4(length(ro - p) * .1);

    // colorful render
    float it = float(ri) / 100.;

    //vec3 c = it*mix(vec3(.6, .2, .5), vec3(.2, .5, .5), length(uv.xy) )*5.;
    vec3 c = it*mix(vec3(.5, .2, .2), vec3(.2, .5, .5), length(uv.xy) )*5.;
    c = c + cos(1e3*uv.y) * .02;

	fragColor = vec4(c, 1.);
}
