void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float TAU = 6.2831853071;

    float angleX = uv.x * TAU;
    float angleY = uv.y * TAU;

    float r = (sin(angleX * cos(angleY + iTime) + iTime) * cos(sin(angleY * 5.0 + angleX) + iTime));
    float g = (cos(cos(angleX) + sin(angleY) + iTime) + sin(sin(angleY * angleX) + iTime));
    float b = 0.4;
    vec4 color = vec4(mix(vec3(255.0, 97.0, 65.0) / vec3(255.0),
                          vec3(0.0, 163.0, 136.0) / vec3(255.0),
                          r - g), 1.0);

	fragColor = color;
}
