vec2	march(vec3 pos, vec3 dir);
vec3	camera(vec2 uv);
void	rotate(inout vec2 v, float angle);
vec3	calcNormal( in vec3 pos, float e, vec3 dir);
float	sdTorus( vec3 p, vec2 t, float phase );
float	mylength(vec2 p);
float	nrand( vec2 n );

float 	t;			// time
vec3	h; 			// light amount
float	mind;


#define I_MAX		100.
#define E			0.0001
#define FAR			50.

/*
* Leon's mod polar from : https://www.shadertoy.com/view/XsByWd
*/

#define PI 3.14159
#define TAU PI*2.

vec2 modA (vec2 p, float count) {
    float an = TAU/count;
    float a = atan(p.y,p.x)+an*.5;
    a = mod(a, an)-an*.5;
    return vec2(cos(a),sin(a))*length(p);
}

// blackbody by aiekick : https://www.shadertoy.com/view/lttXDn

// -------------blackbody----------------- //

// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html

vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

// -------------blackbody----------------- //


void mainImage(out vec4 c_out, in vec2 f)
{
    t  = iTime*.125;
    vec3	col = vec3(0., 0., 0.);
	vec2 R = iResolution.xy,
          uv  = vec2(f-R/2.) / R.y;
	vec3	dir = camera(uv);
    vec3	pos = vec3(0., 0., 10.);

    vec2	inter = (march(pos, dir));
    if (inter.y < FAR)
        col.xyz = vec3(.20,.1, .8)*(inter.y*.5-inter.x*.3);
    else
	    col.xyz = vec3(.50, .52, .27);
    col.xyz += +1.*blackbody(inter.x*30.-inter.y*10.);
    c_out =  vec4(col,1.0);
}

float	scene(vec3 p)
{
    mind = 1e5;

    p.z += 15.;
	rotate(p.xz, -.515*iTime);
    rotate(p.yz, 1.57+sin(.5*iTime)*.125);
    vec3 op = p;
    float	var = (atan(p.x, p.z)+ PI)/(TAU);
    float	vir = 0.;
    p.xz = modA(p.xz, 128.);
    p.xz -= vec2(8., .0);
    vec2 q = vec2(length(p.xy)-4.,p.z);
    vir = (atan(p.x, p.y)+ PI)/(TAU);
    var = abs(var-.5);
    vir = abs(vir-.5);
    float	vir_sound = texture(iChannel0, vec2(floor(vir*128.)/128., .0) ).x;
    float	var_sound = texture(iChannel0, vec2(floor(var*128.)/128., .0) ).x;
    mind = mylength(q)+.5-1. * max(vir_sound,var_sound);

    return (mind);
}

vec2	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec2	s = vec2(0.0, 0.0);
	float dinamyceps = E;
	for (float i = -1.; i < I_MAX; ++i)
	{
		p = pos + dir * dist.y;
		dist.x = scene(p);
		dist.y += dist.x*.5;
        dinamyceps = -dist.x+(dist.y)/(2500.);
        // log trick from aiekick
	    if (log(dist.y*dist.y/dist.x/1e5)>0. || dist.x < dinamyceps || dist.y > FAR)
        {
            break;
        }
	    s.x++;
    }
    s.y = dist.y;
    return (s);
}

float	mylength(vec2 p)
{
	float	ret;

    p = p*p*p*p;
    p = p*p;
    ret = (p.x+p.y);
    ret = pow(ret, 1./8.);

    return ret;
}

// Utilities

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

    return normalize(vec3(
           march(pos+eps.xyy, dir).y - march(pos-eps.xyy, dir).y,
           march(pos+eps.yxy, dir).y - march(pos-eps.yxy, dir).y,
           march(pos+eps.yyx, dir).y - march(pos-eps.yyx, dir).y ));
}
