/*
	--------------------------------
	After Effects / Pulse Effect
	--------------------------------
	Francois 'CoyHot' Grassard - 2017
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv -= 0.5;
	uv /= vec2(iResolution.y / iResolution.x, 1);

	uv.y /= sin(((uv.x+iTime)*10.0)*0.5);

	vec4 p1 = vec4(0.0);

	p1.x = ((cos((uv.x+iTime/10.)*50.)/(uv.y*sin(iTime))/25.)*(sin((uv.x+iTime/10.)*50.)/uv.y*cos(iTime*7.)/25.));
	p1.y = ((cos((uv.x+iTime/9.)*50.)/(uv.y*sin(iTime))/20.)*(sin((uv.x+iTime/11.)*50.)/uv.y*cos(iTime*6.)/25.));
	p1.z = ((cos((uv.x+iTime/8.)*50.)/(uv.y*sin(iTime))/15.)*(sin((uv.x+iTime/12.)*50.)/uv.y*cos(iTime*5.)/25.));

	fragColor = p1;
}
