vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}
vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color) {
	float t = 0.0;
	if ((uv.x > pos.x - width / 2.0) && (uv.x < pos.x + width / 2.0)
		&& (uv.y > pos.y - height / 2.0) && (uv.y < pos.y + height / 2.0)) {
		t = 1.0;
	}
	return vec4(color, t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float gt=iTime;

    vec2 center = vec2(-0.2,.90);
	vec4 bx = rectangle(uv, center, 0.,0., vec3(0));// box ; 

    float fct=0.0,xx=0.2,yy=0.0; // fct , xpox,ypos

    for(int i=0;i<322;i++){// iter
        xx +=0.04;
        fct= sin(18.*gt+(uv.y-uv.x)*5.0)*0.015;;

        bx += rectangle(uv, center-vec2(-xx,yy), 0.02+fct, 0.04+fct,
                        rgb(uv.x-xx-1.,uv.y+xx-0.9,xx*0.8)*2.0);      

        if(xx> 1.1){xx=0.2; yy +=0.06;} // inc yy
     }

	vec4 bg= vec4(rgb(0., 0.0, 0.0), 0.0);
	fragColor = vec4(mix(bg, bx,bx.a));
  }
