#define pow(a, b) pow(abs(a), b)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = iResolution.xy;
    pos = 1.5 * (2.0 * fragCoord - pos) / pos.y;

    float n = 8.0 * pow(sin(iTime), 2.0);
    vec2 pos_n = pow(pos, vec2(n));
    float res = pos_n.x + pos_n.y - 1.0
                / pow(pos_n.x + pos_n.y, 1.0/n - 1.0)
                / length(pos_n / pos);

    fragColor = vec4(1.0 - abs(res));
}
