void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float FrameThinness = 0.9426815260791554983145626690017; //Normalized Frame Density ( Ω ) <- i made up;
    float FrameZoom = 4.0;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    if(cos(mod(uv.x,uv.y)*14.0*FrameZoom) > FrameThinness || cos(mod(uv.y,uv.x)*9.0*FrameZoom) > FrameThinness)
        fragColor = mod(vec4(0.5/(length(uv)+1.0)),vec4(-0.025, 0.025, 0.6, 1.0))+0.9;
    else fragColor = vec4(0.1/(length(sqrt(uv))-2.0)+
                          (vec4(sqrt(uv.x)*sqrt(uv.y)))+vec4(-0.025, 0.025, 0.6, 1.0));
    fragColor += vec4(uv.x / (iResolution.x / iResolution.y), uv.y / 16.0, 1.0, 1.0);
}
