void mainImage( out vec4 O, vec2 U )
{
    O = texelFetch(iChannel0, ivec2(U), 0);
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy; 

    O = texelFetch(iChannel2, ivec2(U), 0);

    vec2 L = vec2( iChannelResolution[ U.x<R.x/2. ? 0 : 1 ].xy ); // blue vs white noise
    vec2 t = fract( iTime*vec2(78.17,17.317) ) * L;    // decorrelated spots along time
  //vec2 t = fract( iTime*vec2(1) ) * L;               // correlated spots along time
    if ( abs(U.y/R.y-.5) < .25 ) U /= 4., R /= 4.;     // zoom
    vec4 T = U.x<R.x/2. ? texelFetch(iChannel0, ivec2(mod(U+t,L)),0)
                        : texelFetch(iChannel1, ivec2(mod(U+t,L)),0);

    if ( U.y < R.y/2. )  T = T.xxxx;                   // B&W

    if ( abs(U.x-R.x/2.) > R.x/4. ) T = step(.5,T);    // binarize

    O = .5 + .99*(O-.5) + .06*(T-.5);                  // relaxation
     // try .96 or .995
}
