void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2. - iResolution.xy) / iResolution.y*2.;

	float k1 = 0.866;
	float k2 = 0.5;

	float tri = max(abs(uv.x)*k1 - uv.y*k2, uv.y);
	float quad = max(abs(uv.x), abs( uv.y));
	float hex = max(max(abs(uv.y*k1 - uv.x*k2), abs(uv.y*k1 + uv.x*k2)), abs(uv.x));
	float circle = length(uv);

    vec3 n = vec3(0);

    if ( tri == -uv.x*k1 - uv.y*k2) 	n = vec3(1,0,0);
	if ( tri == uv.x*k1 - uv.y*k2) 		n = vec3(0,1,0);
	if ( tri == uv.y) 					n = vec3(0,0,1);

    fragColor = clamp(sqrt(texture(iChannel0, -n)*(tri)*1.5),0.,hex);
}
