// Optimized by Fabrice Neyret.

#define h2rgb(c)  abs(fract(c + vec4(3,2,1,0)/3.) * 6. - 3.) - 1.

void mainImage( out vec4 O,  vec2 U )
{
	vec2 R = iResolution.xy;
         U = (U - .5*R) / R.y;
    float j,y, dmin=1e9,d, time=iTime;

    for (int i = 0; i < 100; i++) {
        float y = float(i);
        vec2 point = vec2(sin(y+y/100.*time), 
                          cos(y+y/100.*time))*  y/100./2.;
        d = distance(point,U);
        if (d<dmin) dmin = d, j = y;
    }

    O = h2rgb(j/100.);
}
