void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	float coord = fragCoord.y / iResolution.y;
    float count = 10.0;
    float line = abs(fract(coord * count - 0.5) - 0.5) / fwidth(coord * count);
	float width = 14.0 * (1.0 - coord * coord);
    float l = smoothstep(0.0, 1.0, width - min(line, width));
    vec3 color = vec3(
        1.0 - coord,
        0.2 - coord * 0.2,
        1.2 - coord * 0.8);

	fragColor = vec4(color * l, 1.0);
}
