//number of rows + columns.
//can be non-integer, but will most likely look weird
float numRows = 5.0; 
float numColumns = 8.0;

//show the man?
bool man = true;
float manOpacity = 0.8;

//is a value even?
bool isEven(float value)
{
	return mod(value,2.0)==0.0;
}

//converts a uv coord (0-1) to a tile coord
vec2 uvToTileCoord(vec2 uv, float columns, float rows)
{
    return vec2(floor(uv.x / (1.0/columns)),
                floor(uv.y / (1.0/rows)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord.xy / iResolution.xy;

   //get the tile coords of the uv
   vec2 tile = uvToTileCoord(uv,numColumns,numRows);

   //if x and y are both odd or both even, sample channel0, else sample channel1
   if (isEven(tile.x)==isEven(tile.y))
   {
       fragColor = texture(iChannel0,uv);
   }

   else
   {      
        fragColor = mix(texture(iChannel1,uv+iTime*0.01)*0.5,texture(iChannel0,uv)*0.5,0.4);
   }

    //add the man on top if we want
   if (man)
   {
       vec4 manColor = texture(iChannel2,uv);
       //color of the greenscreen
       vec4 greenColor = vec4(13.0/255.0,163.0/255.0,38.0/255.0,1.0); 
       //compare manColor to greenColor
       float dist = length(manColor.rgb - greenColor.rgb);
       //0.4 is the min distance to cut out
       if (dist>0.4) 
       {
           //blue tint greyscale
           manColor.b = manColor.g = manColor.r * 1.5; 
           //apply man color slightly transparent
           fragColor = mix(fragColor,manColor,manOpacity);
       }        	
   }
}
