void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0,uv);
    if (fragCoord.x==0.0) fragColor = vec4(0.0,0.0,0.0,1.0);
    fragColor/=fragColor.w;
}

#define spectro(v)  texture(iChannel1,vec2((floor(v)+.5)/512.,0)).x
#define gettime()   texture(iChannel0,vec2(0)).x

void mainImage( out vec4 O, vec2 U )
{
    float zoom = 1.,
        scroll = gettime(),
    linepersec = 20./zoom,
          ypos = iTime*linepersec * iResolution.x/512.,
        moveup = floor(ypos-scroll);
    scroll += moveup;

    if (iFrame<2) { O = vec4(0);      return; }
    if (U.x<1.)   { O = vec4(scroll); return; }

    float lx = 1.- (U.x/iResolution.x+(1.-1./zoom)*.5);

    U.y =max( U.y-moveup, 0.);

    O = texture(iChannel0,(U+.001)/iResolution.xy); 

    if (U.y<1. && lx>=0.) 
    {
        if (moveup>0.) O = vec4(0);
        float c = min(spectro(lx *512.*zoom)*2.1, 1.);
        O.x += pow(c,0.8);  // coloring output
        O.y += pow(c,1.2)*0.95;
        O.z += pow(c,1.6)*0.9;
        O.w += 1.;
    }
}
