void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cell_size = 30.;
    int layers = 5;

    vec2 mod_coords = mod(fragCoord, vec2(cell_size));

    float v = round(((cell_size - mod_coords.x) + mod_coords.y) / (cell_size*2.));

    float cell_acc = cell_size;
    for (int i = 0; i < layers; i++) {
        cell_acc *= 2.;
        mod_coords = mod(fragCoord, vec2(cell_acc));
        v *= round(((cell_acc - mod_coords.x) + mod_coords.y) / (cell_acc*2.));
    }

    float stripes = mod((cell_size - fragCoord.x) + fragCoord.y, cell_size) / cell_size;
    if (mod(iTime, 2.) < 1.) {
        if (mod(-iTime, 1.) < stripes) {
            stripes = 1.;
        } else {
            stripes = 0.;
        }
    } else {
        if (mod(-iTime, 1.) > stripes) {
            stripes = 1.;
        } else {
            stripes = 0.;
        }
    }

    vec3 col = vec3(0.3, 0.2, 0.4) * v * stripes;

    fragColor = vec4(col, 1.0);
}
