// Created by Alex Kluchikov

vec2 rot(vec2 p,float a)
{
    float c=cos(a);
    float s=sin(a);
    return p*mat2(s,c,c,-s);
}

void mainImage(out vec4 o, in vec2 uv)
{
    uv/=iResolution.xy;

    vec3 col = clamp(1.-.7*vec3(
        length(uv-vec2(.1,0)),
        length(uv-vec2(.9,0)),
        length(uv-vec2(.5,1))
        ),0.,1.)*2.-1.;
    col.zx=rot(col.zx,iTime*0.65+.2*sin(uv.y*4.+iTime));

    for(int i=0;i<4;i++)
    {
        col.zx=rot(col.zx,iTime*0.65*col.y*0.01);
        col.xy=rot(col.xy,.7+iTime*col.z*0.01);
        col.yz=rot(col.yz,.4-iTime*0.79*col.x*0.01);
    }
    o=vec4(col*.5+.5,1.0);
}
