float devider = 10.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 fractUv = uv;
    fractUv.x = fractUv.x * iResolution.x/iResolution.y ;
    fractUv *= devider;
    fractUv = fract(fractUv);
    vec4 heartBuffer = texture(iChannel0, uv);

    vec4 camera = texture(iChannel1, fractUv);
    if (distance(vec2(0.5 , 0.5), fractUv) > 0.4){
    	camera = vec4(.0, 0.0, 0.0, 1.0);
    }

    if (heartBuffer.r == 1.0){
        heartBuffer.a = 1.0;
    }

    else {
        heartBuffer.a = 0.05;
    }

	vec3 result = mix(vec3(1.0), camera.rgb, heartBuffer.a);
    fragColor = vec4(result, 1.0);

    //fragColor = heartBuffer;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = (2.0*fragCoord-iResolution.xy)/min(iResolution.y,iResolution.x);

    // background color
    vec3 bcol = vec3(1.0,0.8,0.7-0.07*p.y)*(1.0-0.25*length(p));

    // animate
    float tt = mod(0.9,1.5)/1.5;
    float ss = pow(tt,.2)*0.5 + 0.5;
    ss = 1.0 + ss*0.5*sin(tt*6.2831*3.0 + p.y*0.5)*exp(-tt*4.0);
    p *= vec2(0.5,1.5) + ss*vec2(0.5,-0.5);

    // shape
#if 0
    p *= 0.8;
    p.y = -0.1 - p.y*1.2 + abs(p.x)*(1.0-abs(p.x));
    float r = length(p);
	float d = 0.5;
#else
	p.y -= 0.25;
    float a = atan(p.x,p.y)/3.141593;
    float r = length(p);
    float h = abs(a);
    float d = (13.0*h - 22.0*h*h + 10.0*h*h*h)/(6.0-5.0*h);
#endif


    vec3 col = mix( vec3(0.0), vec3(1.0), smoothstep( -0.01, 0.01, d-r) );

    fragColor = vec4(col,1.0);
}
