mat2 rotateMatrix(float angle);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    vec2 st = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    vec4 c_1 = vec4(0.0, 0.9, 0.8, 1.0);
    vec4 c_2 = vec4(0.9, 0.9, 0.9, 1.0);

    mat2 r = rotateMatrix(40.0);
    uv *= r;

    float cl = 5.0;

    uv *= cl;

    float edge = fwidth(st.y) * cl * 2.0;

    float l = abs(fract(uv.x) - 0.5);
    float c = smoothstep(0.3, 0.3 + edge, l);

    //fragColor = vec4(c);
    fragColor = mix(c_1, c_2, c);
}


mat2 rotateMatrix(float angle) {
	return mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
}
