float rowHeight = 0.5f;
float colWidth = 0.2f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 newColor = vec3(0.0,0.0,0.0);

    float offset = (colWidth / 2.0f) * round(mod(uv.y, rowHeight) / rowHeight);

    newColor += round(mod(uv.x + offset, colWidth) / colWidth) * texture(iChannel0, uv).xyz;
    newColor += round(mod(uv.x + offset + (colWidth/2.0f), colWidth) / colWidth) * texture(iChannel1, uv).xyz;

    fragColor = vec4(newColor,1.0);

}
