void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y += 0.02 * iTime;

    //float r = 0.1 * sin(iTime) + uv.x;
    float r = mod(sin(uv.x * 8.0) + cos(uv.y * 9.0) + 0.05 * iTime, 1.0);
    float b = mod(sin(uv.x * 9.0) * cos(uv.y * 6.0) + 0.05 * iTime, 1.0);
    float g = mod(sin(uv.x * 7.0) - cos(uv.y * 4.0) + 0.05 * iTime, 1.0);

    // Output to screen
    fragColor = vec4(r, g, b, 1.0);
}
