// Rotate
mat2 rot(float a){
    return mat2(cos(a), -sin(a),
                sin(a), cos(a));
}


// SDF Sphere
float sphere(vec3 p, float radius){
    return length(p)-(radius*(tan(iTime*0.2)+6.));

}



// Map distance to all objects in the scene
float map(vec3 p)
{
    float radius = 0.1;

    // Transform coordinate space so spheres repeat
    //vec3 q = fract(p) * 2.0 - 1.0;
    vec3 q = fract(p)-0.5;

    // Signed distance of sphere
    return sphere(q, radius);
}


float trace(vec3 origin, vec3 r) 
{
  float t = 4.0;
    for (int i = 0; i < 64; ++i) {
        vec3 p = origin + r * r* t *0.7 -sin(iTime);
        float d = map(p)*2.;
        t -= d;
        }
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv *2.-1.; // Remap the space to -1. to 1.
    uv.x *= iResolution.x/iResolution.y;

   /* if (mod(iTime,2.)=1.){
   	float FOV = 0.5;
    }*/
    //else{
    float FOV = 3.;

  //  }
   	vec3 ray = normalize(vec3(uv, FOV));

    // Rotate Y towards Z
    ray.yz *= rot(iTime*0.1);
    ray.zx *= rot(iTime*0.1);

    vec3 origin = vec3(10.0, 0.0, iTime);
    float t = trace(origin, ray);


    float linearFog = 2.0 / (1.0 - t*2.);
    float expFog = 2.0 / (t*t*0.1);

    vec3 fc = vec3(iTime+iTime+uv.xy+vec2(5,3),expFog*7.);
    //vec3 fc = vec3(expFog*7.);

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output pixels to screen
    fragColor = vec4(fc,1.0);

    // Try oscilating the final pixels
    fragColor = vec4(cos(fc*0.7)-sin(fc*0.7),6.0);
}
