float stripe_t( float t, float origin, float width, float smoothness ) {
    // t for a simple stripe
    // origin
    // width
    float hw = width / 2.0;
    float blend = smoothstep(origin - hw, 
                             origin+smoothness - hw, 
                             t) * 
        smoothstep(origin+hw, origin+hw-smoothness, t);
    return blend;
}

#define NUM_STRIPES 5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 bgcolor = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 color = vec4(0.9, 0.9, 0.0, 1.0);
    float smoothness = 0.01;

    // simplest example
    // fragColor = vec4(mix(bgcolor, color, stripe_t(uv.x, 0.5, 0.1, smoothness)));   

    // Make some wiggly stripes
    vec4 col = vec4(bgcolor);
    for(int i = 1; i < NUM_STRIPES+1; i++) {        
		col = vec4(mix(col, color, stripe_t(uv.x, .5 + (sin(cos(iTime * float(i)) * (float(i)+uv.y -.5) * .4) * .25), 0.1, smoothness)));
    }
    fragColor = col;
}
