#define halfpi (0.5*pi)
#define pi 3.14159265
#define twopi (2.0*pi)

const float zoom = 1.0;
const float flare_freq = 1.5;
const float flare_speed = -0.3;
const float flare_amp = 0.15;
const float wibble_freq = 10.0;
const float wibble_speed = 0.2;
const float wibble_amp = 0.004;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = zoom * fragCoord.xy / iResolution.yy;
    float dist = length(vec2(zoom / 2.0 * iResolution.x / iResolution.y, zoom / 2.0) - uv);
    vec2 phase = vec2(0.0, halfpi);
    vec2 flare = flare_amp * cos(phase + twopi * (flare_freq * dist + 2.0 * uv + flare_speed * iTime));
    vec2 wibble = wibble_amp * cos(phase + twopi * (wibble_freq * dist + wibble_speed * iTime));

    fragColor = texture(iChannel0, uv + wibble);
    fragColor.rg = fragColor.rg * (1.0 - flare_amp + flare);
}
