#define RADIUS 0.5
#define AB_SCALE 0.75

float diskColor(in vec2 uv, vec2 offset)
{
    uv = uv - smoothstep(0.01,2.0,texture(iChannel0, (uv*1.0 - vec2(iTime /3.0,0)) + offset).r) * 0.20;

    float d = length(uv) - RADIUS;
    return smoothstep(0.01,0.02,d);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (-iResolution.xy + 2.0 * fragCoord) / iResolution.y;

    vec3 color = vec3(0);
    color += vec3(1, 0, 0)*diskColor(uv, vec2(0, 0.02) * AB_SCALE);
    color += vec3(0, 1, 0)*diskColor(uv, vec2(0.01, 0.01) * AB_SCALE);
    color += vec3(0, 0, 1)*diskColor(uv, vec2(0.02, 0) * AB_SCALE);

    fragColor = vec4(color, 1.0);
}
