void mainImage( out vec4 f, in vec2 w ) {

	vec2  r = iResolution.xy, p = w - r*.5;
	float d = length(p) / r.y, c=1., x = pow(d, .1), y = atan(p.x, p.y) / 6.28;

    vec4 col;
	for (int i = 0; i < 3; ++i)    
    {
		c = min(c, length(fract(vec2(x - iDate.w*float(i)*.005, fract(y + float(i)*.125)*.5)*20.)*2.-1.));
        col[i]=c;
    }
	f = vec4(d+20.*c*d*d*(.6-d)*col);
}
