void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //// Заливка красным весь экран ////
	/*
    fragColor = vec4(1.0, 0.0, 0.0, 1.0);
	*/


    //// Задание цвета через переменную ////
    /*
    // Переменная
    vec4 solidBlue = vec4(0.0, 0.0, 1.0, 1.0);
    fragColor = solidBlue;
	*/

    //// Работа с XY координатами ////

	/*    
	// Примечание
	// Шейдер работает как постоянный цикл.
	// Т.е. данные действия будут вызываться для каждого переданного пикселя

    // Координаты текущего пикселя
    vec2 xy = fragCoord.xy;
    // Это результирующая переменная. На данный момент экран черный
    vec4 solidBlack = vec4(0.0, 0.0, 0.0, 1.0);
    // Если текущий пиксель больше 300 по оси X
    if (xy.x > 300.0) {
        // То делаем его красноватым
        solidBlack.r = 0.5;
    }

    if (xy.y > 100.0) {
        solidBlack.b = 0.5;
    }
    // Теперь результат - черный с красными пятнами
    fragColor = solidBlack;
    */


    //// Работа с картинкой ////

    // Соединяем это в одну строку
    //vec2 xy = fragCoord.xy / iResolution.xy;
    // Получаем от iChannel0 пиксель в координате xy
    //vec4 texColor = texture(iChannel0,xy);

    // texColor.b = xy.x;
    //texColor.r = abs(xy.x / 0.5);
    //texColor.g = abs(xy.y / 0.5);


    // Присваиваем экранному пикселю этот цвет
    //fragColor = texColor;



    vec2 position = (gl_FragCoord.xy / vec2(1920, 1080).xy) / 4.0;

    float color = 0.0;
    color += sin(position.x * cos(iTime / 15.0) * 80.0) + cos(position.y * cos(iTime / 15.0) * 10.0);
    color += sin(position.y * sin(iTime / 10.0) * 40.0) + cos(position.x * sin(iTime / 25.0) * 40.0);
    color += sin(position.x * sin(iTime / 5.0) * 10.0) + sin(position.y * sin(iTime / 35.0) * 80.0);
    color *= sin(iTime / 10.0) * 0.5;

    fragColor = vec4(vec3(color, color * 0.5, sin(color + iTime / 3.0) * 0.75), 1.0);
}
