//2D explosion test

//noise by iq https://www.shadertoy.com/view/XdXGW8
vec2 hash( vec2 x )
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

//***************************************************


vec3 Circle(vec2 uv, vec2 p, float r, float blur, vec3 color) {

    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);
    color = vec3(color*c);
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy; // 0 <> 1

	uv -= .5; // -0.5 <> 0.5
    uv.x *= iResolution.x/iResolution.y;

    float noiseboi = 0.0;
	noiseboi = noise( 32.0*uv );

    vec2 p = vec2(0);
    float r = (mod(iTime, 2.));
    float dissolve = r;
    noiseboi *= dissolve;
    vec3 color = vec3(0);
    float ir = clamp(-1.*mod(iTime,2.)+1., 0.04, 1.0); 

    if (r < .3){ 
        color = vec3(1., .8, .5);
    } else {
        color = vec3(1., .3, .02);
    }
    vec3 c = Circle(uv, p, r, 0.04, color);
	c -= Circle(uv, p, r, ir, color);
    c *= 2.;
    noiseboi *= c.x+c.y+c.z; 
    c += noiseboi;
    c += Circle(uv, p, -1.*mod(iTime,2.)+.85, .8, vec3(1.0, .84, .23));


    fragColor = vec4(c,1.0);
}
