void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //point1
    float diameter1 = 100.0f;
    vec2 origo1 = vec2( iMouse.x, iMouse.y);
    //vec2 origo1 = vec2( iResolution.x*( (1.0f + sin(iTime/1.2f))/2.0f ), iResolution.y/2.0f);
    vec2 pos1 =  vec2(fragCoord.x - origo1.x, fragCoord.y - origo1.y );
    float dist1 = sqrt( (pos1.x*pos1.x )+(pos1.y*pos1.y) ) / diameter1 * 1.0f;
    //point2
    float diameter2 = 75.0f;
    vec2 origo2 = vec2( iResolution.x*1.0f/4.0f, iResolution.y/2.0f);
    vec2 pos2 =  vec2(fragCoord.x - origo2.x, fragCoord.y - origo2.y );
    float dist2 = sqrt( (pos2.x*pos2.x )+(pos2.y*pos2.y) ) / diameter2 * 1.0f;
   	//point3
    float diameter3 = 100.0f;
    vec2 origo3 = vec2( iResolution.x*2.0f/4.0f, iResolution.y/2.0f);
    vec2 pos3 =  vec2(fragCoord.x - origo3.x, fragCoord.y - origo3.y );
    float dist3 = sqrt( (pos3.x*pos3.x )+(pos3.y*pos3.y) ) / diameter3 * 1.0f;
    //point4
    float diameter4 = 150.0f;
    vec2 origo4 = vec2( iResolution.x*3.0f/4.0f, iResolution.y/2.0f);
  	vec2 pos4 =  vec2(fragCoord.x - origo4.x, fragCoord.y - origo4.y );
    float dist4 = sqrt( (pos4.x*pos4.x )+(pos4.y*pos4.y) ) / diameter4 * 1.0f;
    //col
    float rep = 1.f;
    //float c = 1.0f/(dist1+dist2+dist3+dist4);
    vec3 rgb = vec3(dist2,dist3,dist4);
    vec4 col = vec4( ( vec3( (1.0f- pow(dist1, rep) )*(1.0f-pow(dist2, rep))*(1.0f-pow(dist3, rep))*(1.0f-pow(dist4, rep)) ) ), 1);

    float interv = mod(fragCoord.x, 2.0f)*mod(fragCoord.y, 2.0f);
    fragColor =  1.f-floor( col );//*vec4(rgb,1);//vec4(interv,interv,interv,1);
}
