// create by dalerank, a bit faster then https://www.shadertoy.com/view/MtyXRK
void mainImage( out vec4 f, vec2 fragCoord )
{
    vec2 xy = fragCoord / iResolution.y;
    xy /= 0.1;
    vec2 p = xy, a; f = vec4(1, 1, 1, 1);
    vec2 gp, gpsin, gpf;
	//vec2 p = g /= iResolution.y / 8., a; f-=f-9.; // screen size depend

    for(int x=-3;x<=3;x++)
        for(int y=-3;y<=3;y++)
        {
            p = vec2(y,x);
            gp = floor(xy)+p;
            gpf = vec2(gp.y, gp.x) * vec2(sin(gp.x+gp.x), sin(gp.y+gp.y));
            a.x = sin(iTime/5.0 * gpf.y);
            a.y = sin(iTime/3.0 * gpf.x);
            p += .5 + .5 * a - fract(xy);
            f = min(f, length(p * cos(a.x*1.5)));
        }

    f = sqrt(vec4(10,5,2,1)*f); // 249c => i prefer original color and i prefer color with 10 instead of 9 :)
    //f *= vec4(8,5,3,1); // 241c => similar for code size
}
