void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec2 size = vec2(0.0,28.0);
    float wiggleSP = 2.0;
    size.x += clamp(sin(iTime),0.0,1.0)*.1;
    //blend *= iTime*.5;
    uv.x += (sin(uv.y*size.y+(iTime*wiggleSP))*size.x);
	vec4 img0 = texture(iChannel0,uv);
    vec4 img1 = texture(iChannel1,uv);
    float blend = clamp(sin(iTime*.5+5.583)+0.5,0.0,1.0);
    vec4 col = mix(img0,img1,blend);
    // Output to screen
    fragColor = col;
}
