// Rotate
mat2 rot(float a){
    return mat2(cos(a), -sin(a),
                sin(a), cos(a));
}


// SDF Sphere
float sphere(vec3 p, float radius){
    return length(p)-radius;

}


// Map distance to all objects in the scene
float map(vec3 p)
{
    float radius = 0.1;

    // Transform coordinate space so spheres repeat
    vec3 q = fract(p)*0.8 -0.5;

    // Signed distance of sphere
    return sphere(q, radius);
}


float trace(vec3 origin, vec3 r) 
{
  float t = 0.0;
    for (int i = 0; i < 64; ++i) {
        vec3 p = origin + r * t;
        float d = map(p);
        t += d;
        }
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv *2.-1.; // Remap the space to -1. to 1.
    uv.x *= iResolution.x/iResolution.y;


   	float FOV = 1.2;
   	vec3 ray = normalize(vec3(uv, FOV));

    // Rotate Y towards Z
    ray.yz *= rot(iTime*0.1);
    ray.zx *= rot(iTime*0.2);

    vec3 origin = vec3(0.0, 0.0, iTime);
    float t = trace(origin, ray);


    float linearFog = 2.0 / (1.0 - t*0.2);
    float expFog = 2.0 / (t*t*0.1);

    vec3 color = vec3(0.0, iTime, -iTime);

    vec3 fc = vec3(expFog) / cos(color);
    //vec3 fc = vec3(expFog) / cos(color)- color*0.7;

    // Output pixels to screen
    fragColor = cos(vec4(fc,2.0));


}
