float dimension = 10.0; // the dimension of the length and width
void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
vec2 uv = fragCoord/iResolution.xy; // normalize the coordinate input

vec4 splitPic = texture(iChannel0, uv); // the background texture

float xTile = mod(floor(uv.x * dimension), 2.0);

if(mod(floor(uv.y * dimension), 2.0) == 0.0) //if the row is even
{
    if(xTile == 0.0) // and the column is even
		splitPic = texture(iChannel1, uv); // set that area to a texture
}
else // if the row is odd
    if(xTile - 1.0 == 0.0) // and the column is odd
		splitPic = texture(iChannel1, uv); // set that area to a texture

fragColor = splitPic; // print textures
}
