#define PI 3.14159265358979323

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 uv = fragCoord.xy / iResolution.xy;
    float a1 = clamp((uv.x*2.)*((1.-uv.x)*2.),0.,1.)+1.;
    float b1 = (1.-clamp((uv.x*2.)*((1.-uv.x)*2.),0.,1.))/4.;
    vec2 uv2 = vec2(uv.x,((uv.y-0.5)*a1)+0.5);
    vec4 utexture = texture(iChannel0,fract(uv2+(time*0.1)));
    vec3 colors = vec3(utexture+(uv2,0.))-b1;

    vec2 suv = (2. * uv - 1.) * vec2(iResolution.x / iResolution.y,1.);
    vec2 suv2 = (((uv)-0.5)*vec2(1.7,1.))+0.5;
    float a2 = (1./length(suv));
    float atans = atan(suv.x,suv.y)/PI;
    float a3 = fract(pow(a2,0.1)*5.-(time/3.));
    vec2 uv3 = vec2(fract(atans*2.+(time/10.)),a3*3.);
    vec4 texture2 = texture(iChannel0,fract(uv3));
    float light = ((length(suv2-0.5)-0.5)*1.8)/2.;
    vec3 colors2 = clamp(texture2.rgb+(light),0.,1.);
    //colors2 = vec3(texture2);

    float sintime = clamp(sin(time)*6.,0.,1.);

	fragColor = vec4(mix(colors,colors2,sintime),1.0);
}
