void mainImage(out vec4 o, in vec2 n)
{
	vec2  u = n / iResolution.xy , p = -1. + 2. * u;
	float t = iTime, 
          a = atan(p.y, p.x) , 
          r = length(p) , 
          c = .7 * cos(t + 7. * a);
	o = vec4(
        texture(
            iChannel0, 
            vec2(7. * a / 3.14, -t + sin(7. * r + t) + c) * .5)) * (.5 + .5 * (sin(t + 7. * r) + c));
}
